mapper.select:
select * from image

	
		id = #{id}
	
	
		and albumId = #{albumId}
	

order by modifiedTime desc;

mapper.update:
update image
fname=#{fname}, url=#{url}, title=#{title}, fsize=#{fsize}, description=#{description}, modifiedTime=#{modifiedTime}, modifiedUserName=#{modifiedUserName}, albumId=#{albumId}
where id = #{id}
